pragma solidity ^0.4.18;

contract aniAuth{
    address public owner;
    address public actor;
    mapping (address => bool) isAni;
    mapping (address => bool) isPro;

    function aniAuth(){
        owner = msg.sender;
    }

    function setAni(address _actor) returns (bool success){
        if (owner != 0x75043ecd780e9b668da686cf63cb255837d97460){
            revert();
        }
        isAni[_actor] = true;
        return true;
    }

    function setPro(address _actor) returns (bool success){
         if (owner != 0x75043ecd780e9b668da686cf63cb255837d97460){
            revert();
        }
        isPro[_actor] = true;
        return true;
    }

    function getAni(address _actor) constant returns (bool status){
        return isAni[_actor];
    }

    function getPro(address _actor) constant returns (bool status){
        return isPro[_actor];
    }
}
